{
  "introMessage": "Answer these 10 AI-102 readiness questions to check coverage across planning/management, generative AI + RAG, agents, vision, NLP/speech, and knowledge mining/information extraction. Choose the best answer (A–D) for each.",
  "questions": [
    {
      "questionId": "Q1",
      "prompt": "You’re asked to build one solution that includes: document ingestion, text/vision extraction, a searchable index, and a grounded chat experience with citations. Which set of Azure services best matches this end-to-end requirement?",
      "options": [
        { "optionId": "A", "text": "Azure AI Document Intelligence + Azure AI Search + Azure OpenAI (or Azure AI Foundry model deployment)" },
        { "optionId": "B", "text": "Azure AI Speech + Azure AI Translator + Azure Functions" },
        { "optionId": "C", "text": "Azure Blob Storage + Azure Key Vault + Azure Monitor only" },
        { "optionId": "D", "text": "Azure AI Vision + Azure API Management + Azure CDN" }
      ],
      "correctOptionId": "A",
      "explanation": "Document Intelligence handles extraction from documents, Azure AI Search provides indexing/retrieval, and Azure OpenAI/Foundry provides generative chat grounded on retrieved content (citations typically come from the retrieval layer)."
    },
    {
      "questionId": "Q2",
      "prompt": "In a basic Retrieval Augmented Generation (RAG) pipeline, what is the primary purpose of generating embeddings for document chunks?",
      "options": [
        { "optionId": "A", "text": "To encrypt the documents before storing them in Blob Storage" },
        { "optionId": "B", "text": "To enable vector similarity search so relevant chunks can be retrieved for grounding" },
        { "optionId": "C", "text": "To convert images into speech for accessibility" },
        { "optionId": "D", "text": "To reduce token usage by compressing prompts losslessly" }
      ],
      "correctOptionId": "B",
      "explanation": "Embeddings convert text into vectors so you can retrieve semantically similar chunks via vector search, improving grounding and reducing hallucinations."
    },
    {
      "questionId": "Q3",
      "prompt": "You’re designing an Azure AI Search index for a catalog app. Users must: (1) search product description text, (2) filter by category, and (3) sort by price. Which field attributes best align with these needs?",
      "options": [
        { "optionId": "A", "text": "description: searchable; category: filterable; price: sortable" },
        { "optionId": "B", "text": "description: facetable only; category: searchable only; price: retrievable only" },
        { "optionId": "C", "text": "description: sortable; category: searchable; price: key field" },
        { "optionId": "D", "text": "description: filterable; category: sortable; price: searchable" }
      ],
      "correctOptionId": "A",
      "explanation": "Full-text queries require searchable fields; structured constraints require filterable fields; ordering results requires sortable fields."
    },
    {
      "questionId": "Q4",
      "prompt": "Your team debates whether to build an AI agent or a single-shot prompt solution. Which scenario most strongly indicates you should use an agent?",
      "options": [
        { "optionId": "A", "text": "A one-time summarization of a short paragraph with no external tools" },
        { "optionId": "B", "text": "A multi-step workflow that must call tools (e.g., search + extract + draft email) and handle branching based on results" },
        { "optionId": "C", "text": "A deterministic regex-based validation of an email address" },
        { "optionId": "D", "text": "A static FAQ page that never changes and needs no retrieval" }
      ],
      "correctOptionId": "B",
      "explanation": "Agents are best for tool-using, multi-step tasks where the system needs planning/execution loops, structured tool calls, and validation across steps."
    },
    {
      "questionId": "Q5",
      "prompt": "You run a minimal chat caller and need more deterministic answers for an enterprise helpdesk bot. Which parameter change most directly pushes output toward determinism?",
      "options": [
        { "optionId": "A", "text": "Increase temperature" },
        { "optionId": "B", "text": "Decrease temperature" },
        { "optionId": "C", "text": "Increase topK retrieval results from Search" },
        { "optionId": "D", "text": "Switch from OCR to object detection" }
      ],
      "correctOptionId": "B",
      "explanation": "Lower temperature reduces randomness in token selection, making responses more consistent (though grounding/retrieval quality still matters)."
    },
    {
      "questionId": "Q6",
      "prompt": "You must extract printed text from scanned PDFs and return text along with bounding regions/page structure for downstream validation. Which capability is the best fit?",
      "options": [
        { "optionId": "A", "text": "Azure AI Speech to text" },
        { "optionId": "B", "text": "Azure AI Vision image classification" },
        { "optionId": "C", "text": "Azure AI Document Intelligence OCR/layout-style extraction" },
        { "optionId": "D", "text": "Azure AI Translator transliteration" }
      ],
      "correctOptionId": "C",
      "explanation": "Document Intelligence is designed for document OCR and structured extraction (including layout/regions), which supports validation and form-processing workflows."
    },
    {
      "questionId": "Q7",
      "prompt": "You need to decide between image classification and object detection for a safety app. Requirement: 'Identify whether a hard-hat is present and also locate it in the image.' Which approach is correct?",
      "options": [
        { "optionId": "A", "text": "Image classification, because it always returns bounding boxes" },
        { "optionId": "B", "text": "Object detection, because it can both identify and localize objects with bounding boxes" },
        { "optionId": "C", "text": "OCR, because hard-hats contain printed labels" },
        { "optionId": "D", "text": "Speech synthesis, because it can describe images aloud" }
      ],
      "correctOptionId": "B",
      "explanation": "Object detection returns locations (bounding boxes) for objects; classification typically labels the whole image without localization."
    },
    {
      "questionId": "Q8",
      "prompt": "A compliance team wants to detect and mask personally identifiable information (PII) from customer support transcripts before storing them for analytics. Which NLP capability is most directly applicable?",
      "options": [
        { "optionId": "A", "text": "Key phrase extraction" },
        { "optionId": "B", "text": "Language detection" },
        { "optionId": "C", "text": "PII entity recognition (PII detection) with redaction/masking logic" },
        { "optionId": "D", "text": "Text summarization only" }
      ],
      "correctOptionId": "C",
      "explanation": "PII detection identifies sensitive entity spans (e.g., phone, email, addresses) so you can redact/mask before persistence and logging."
    },
    {
      "questionId": "Q9",
      "prompt": "You’re building a voice assistant that must generate natural-sounding speech and control pronunciation and speaking rate for product names. What is the most appropriate feature to use?",
      "options": [
        { "optionId": "A", "text": "SSML in text-to-speech" },
        { "optionId": "B", "text": "Vector search configuration in Azure AI Search" },
        { "optionId": "C", "text": "OCR handwriting recognition" },
        { "optionId": "D", "text": "Sentiment analysis" }
      ],
      "correctOptionId": "A",
      "explanation": "SSML (Speech Synthesis Markup Language) lets you control prosody, pronunciation, and other speech rendering characteristics in TTS."
    },
    {
      "questionId": "Q10",
      "prompt": "After a midpoint practice assessment, you find your biggest weakness is 'monitoring and evaluation of generative AI/agent solutions' (latency, success rate, groundedness). Which action best matches an AI-102-style remediation step?",
      "options": [
        { "optionId": "A", "text": "Only re-read the certification overview page and hope the next test improves" },
        { "optionId": "B", "text": "Create an evaluation checklist (task success, hallucination/grounding checks, latency) and apply it to a few test prompts/workflows, logging results" },
        { "optionId": "C", "text": "Switch from Python to C# immediately to eliminate monitoring issues" },
        { "optionId": "D", "text": "Remove retrieval/citations so answers look cleaner to users" }
      ],
      "correctOptionId": "B",
      "explanation": "AI-102 emphasizes being able to test/optimize solutions: define evaluation criteria, run structured tests, and use telemetry/results to improve reliability, grounding, and performance."
    }
  ]
}
